import { useReducer } from "react";
import CartContext from "./Cart-Context";

// State
const DefaultCartState = {
  Items: [],
  TotalAmount: 0,
};
// Action
const cartReducer = (state, action) => {
  if (action.type === "ADD_TO_CART") {
    const UpdateItems = state.Items.concat(action.item);
    const UpdateTotalAmount = state.TotalAmount + action.item.price;

    // Finding Exist idex of Item in Array , Here we check of theres is a multiple item in array
    const ExistingIndexOfItemInCart = state.items.findIndex(
      (item) => item.id === action.item.id
    );
    // Looking for exiting Cart Item if Thres is already that item in array
    const ExistingCartItem = state.items[ExistingIndexOfItemInCart];
    let UpdateItem;
    // let UpdateItems;

    if (ExistingCartItem) {
      UpdateItem = {
        ...ExistingCartItem,
        amount: ExistingCartItem.amount + action.item.amount,
      };
    }

    return {
      Items: UpdateItems,
      TotalAmount: UpdateTotalAmount,
    };
  }
  return DefaultCartState;
};

const CartProvider = (props) => {
  // Use Cart Reducer
  const [cartState, DispatchCartState] = useReducer(
    cartReducer,
    DefaultCartState
  );

  // Adding Items to the Cart
  const AddItemHandler = (item) => {
    DispatchCartState({
      type: "ADD_TO_CART",
      item: item,
    });
  };

  // Removing items in Cart based on ID
  const RemoveItemHandler = (id) => {};

  // CartContext Helper
  const cartContext = {
    Items: cartState.Items,
    TotalAmount: cartState.TotalAmount,
    AddItem: AddItemHandler,
    RemoveItem: RemoveItemHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
